def solution(number: str, k: int) -> str:
    '''
        https://programmers.co.kr/learn/courses/30/lessons/42883
    '''
    answer: str = ''

    final: int = len(number) - k
    index: int = 0
    check_case: list = ['9', '8', '7', '6', '5', '4', '3', '2', '1', '0']
    while final > 0:
        if len(number) - index == final:
            answer += number[index:]
            break

        for i in check_case:
            if i in number[index: len(number) - final + 1]:
                answer += i
                index += number[index:].index(i) + 1
                final -= 1
                break
    return answer

print(solution("1231234", 3))
print(solution("4177252841", 4))
print(solution("", 999))